mapper.update:
update community set co_title = #{community.co_title},
					co_cc_num = ${cc_num},
					co_info = #{community.co_info}
where co_num = ${co_num}

		update community
    		set co_views = co_views + 1
   	    where co_num = #{co_num}

update community
	set co_reply = (select count(*) from reply where re_co_num = #{co_num})
where co_num = #{co_num}

	    UPDATE community
	    SET co_like = co_like + 1
    	WHERE co_num = #{coNum}
